mapper.select:
SELECT COUNT(*) FROM PROGRAM P LEFT JOIN USERS USING (USER_NO) WHERE P.STATUS='Y'
AND
PROGRAM_NAME LIKE '%${keyword}%'
USER_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
AND ACTIVITY_TYPE = #{activityType}
AND ACTIVITY_LOCATION LIKE '%${activityLocation}%'
AND ACTIVITY_TIME = #{activityDays}
AND ACTIVITY_DAYS = #{activityDays}

mapper.insert:
INSERT INTO REQUEST 
(REQUEST_NO, REQUEST_DATE, STATUS, USER_NO, PROGRAM_NO)
VALUES (
	SEQ_REQNO.nextval, SYSDATE, DEFAULT, '2', #{programNo}
)

mapper.select:
SELECT PROGRAM_NO, USER_NO FROM LIKES
WHERE PROGRAM_NO = #{programNo}
AND USER_NO = '2'
