mapper.update:
UPDATE DONATE_BOARD
   SET COUNT = COUNT + 1
 WHERE DONATE_NO = #{donateNo}

mapper.insert:
INSERT INTO ATTACHMENT (FILE_NO,ORIGIN_NAME,CHANGE_NAME,REF_FNO,FILE_TYPE) SELECT SEQ_ATTNO.NEXTVAL,C.* FROM(
SELECT #{attachment.originName}, #{attachment.changeName}, #{attachment.refFno}, 'D' FROM DUAL
) C

mapper.update:
UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
	CHANGE_NAME = #{changeName}
WHERE REF_FNO = #{refFno}

UPDATE DONATE_BOARD
SET STATUS = 'N'
WHERE DONATE_NO = #{donateNo}
