mapper.select:
SELECT COUNT(*) FROM PROGRAM P LEFT JOIN USERS USING (USER_NO) WHERE P.STATUS='Y'
AND
PROGRAM_NAME LIKE '%${keyword}%'
USER_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
AND SUBSTR(ACTIVITY_LOCATION, 1,1) = #{active_location}
AND ACTIVITY_TIME = #{active_time}
AND ACTIVITY_DAYS = #{active_days}

SELECT PROGRAM_NO, PROGRAM_NAME, TO_CHAR(PROGRAM_ACTIVITY_START_DATE, 'YYYY"년" MM"월" DD"일"') PROGRAM_ACTIVITY_START_DATE, TO_CHAR(PROGRAM_ACTIVITY_END_DATE, 'YYYY"년" MM"월" DD"일"') PROGRAM_ACTIVITY_END_DATE, ACTIVITY_DAYS, ACTIVITY_TIME, SUBSTR(ACTIVITY_LOCATION,3) AS ACTIVITY_LOCATION, PROGRAM_ENROLL_END_DATE
FROM PROGRAM P
WHERE STATUS = 'Y'
AND PROGRAM_NAME LIKE '%${keyword}%'
AND USER_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%')
AND SUBSTR(ACTIVITY_LOCATION, 1,1) = #{active_location}
AND ACTIVITY_TIME = #{active_time}
AND ACTIVITY_DAYS = #{active_days}
order by 1 desc

mapper.insert:
INSERT INTO REQUEST
(REQUEST_NO, REQUEST_DATE, STATUS, USER_NO, PROGRAM_NO)
VALUES (
SEQ_REQNO.nextval, SYSDATE, DEFAULT, #{userNo}, #{programNo}
)

mapper.select:
SELECT COUNT(*)
FROM LIKES
WHERE PROGRAM_NO = #{programNo}
AND USER_NO = #{userNo}

SELECT COUNT(*) FROM REQUEST
WHERE PROGRAM_NO = #{programNo}
AND USER_NO = #{userNo}

SELECT PROGRAM_NO
FROM PROGRAM
	WHERE USER_NO = #{userNo} AND
	PROGRAM_NAME = #{programName}
	AND PROGRAM_ACTIVITY_START_DATE = #{programActivityStartDate}

SELECT CHAT_NO
FROM CHAT
	WHERE PROGRAM_NO = #{programNo}
